mapper.select:
SELECT t1.id, t1.name, t1.title, t1.menu_level, t1.pid, t1.icon, t1.sort, t1.is_deleted, t1.create_time, t1.update_time, t1.hidden, t1.component, t1.path, t1.redirect FROM t_menu t1 LEFT JOIN t_role_menu t2 ON t1.id=t2.menu_id WHERE t1.is_deleted=0 AND t1.menu_type != 2 AND t2.role_id IN
#{role.id}
ORDER BY t1.sort
