mapper.select:
SELECT t1.id, t1.title, t1.summary, t1.content, t1.click_count,
t1.like_count, t1.file_id, t1.create_time, t1.update_time, t1.admin_id, t1.author, t1.blog_sort_id, t1.`level`, t1.sort, t1.open_comment, t1.is_deleted
FROM t_blog t1


        LEFT JOIN t_blog_tag t2
        ON t1.id = t2.blog_id
        WHERE t2.tag_id = #{tagId,jdbcType=VARCHAR}


        WHERE 1=1



AND t1.is_deleted = 0

    AND t1.title = #{title,jdbcType=VARCHAR}


    AND t1.author = #{author,jdbcType=TINYINT}


    AND t1.level = #{level,jdbcType=TINYINT}


    AND t1.open_comment = #{openComment,jdbcType=TINYINT}


    AND t1.blog_sort_id = #{blogSortId,jdbcType=TINYINT}

 ORDER BY t1.sort DESC
 LIMIT #{page,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

SELECT COUNT(t1.id)
FROM t_blog t1


        LEFT JOIN t_blog_tag t2
        ON t1.id = t2.blog_id
        WHERE t2.tag_id = #{tagId,jdbcType=VARCHAR}


        WHERE 1=1



AND t1.is_deleted = 0

    AND t1.title = #{title,jdbcType=VARCHAR}


    AND t1.level = #{level,jdbcType=TINYINT}


    AND t1.open_comment = #{openComment,jdbcType=TINYINT}


    AND t1.blog_sort_id = #{blogSortId,jdbcType=TINYINT}
